// ✅✅ --- بداية التعديل --- ✅✅
/// اختيار حبكة عشوائية وإرجاعها كـ Map يحتوي على title و plot.
Map<String, String> getRandomPlotWithTitleFromMap(Map<String, List<String>> data) {
  final allPlots = data.values.expand((list) => list).toList();
  if (allPlots.isEmpty) {
    return {"title": "خطأ", "plot": "لا توجد حبكات متاحة الآن."};
  }

  final r = Random();
  final randomFullText = allPlots[r.nextInt(allPlots.length)];

  // محاولة فصل العنوان (الجزء قبل أول ":")
  final parts = randomFullText.split(':');
  if (parts.length > 1) {
    // وجدنا ":" - افترض أن الجزء الأول هو العنوان والباقي هو النص
    final title = parts[0].trim();
    final plot = parts.sublist(1).join(':').trim(); // أعد ضم الأجزاء المتبقية إذا كان هناك أكثر من ":"
    // تأكد أن العنوان ليس فارغًا جدًا
    if (title.isNotEmpty && plot.isNotEmpty) {
       return {"title": title, "plot": plot};
    }
  }

  // إذا لم نجد ":" أو كان الفصل غير ناجح، أرجع النص كاملاً كـ plot وعنوان افتراضي
  return {"title": "حبكة عشوائية", "plot": randomFullText};
}
// ✅✅ --- نهاية التعديل --- ✅✅


List<String> getCategories(Map<String, List<String>> data) {
  return data.keys.toList();
}